INSERT OVERWRITE TABLE pc_summary PARTITION (report_date) SELECT translate(concat('${report.date}',report),'-','') as reportkey, cast(report as bigint) as report, name, title, regexp_replace(name, '^PCAR-([^-]+)-([^-]+)-(.+)$', '$1') as segment, case when name like 'PCAR%' then regexp_replace(name, '^PCAR-([^-]+)-([^-]+)-(.+)\\s*$', '$2') when name like 'PAIR%' then regexp_replace(name, '^PAIR-([^-]*)-(.+)$', '$1') else '' end as policy, case when name like 'PCAR%' then regexp_replace(name, '^PCAR-([^-]+)-([^-]+)-(.+)$', '$3') when name like 'PAIR%' then regexp_replace(name, '^PAIR-([^-]*)-(.+)$', '$2') else '' end as platform, locking, cast(regexp_replace(modified, '^(\\d+)/(\\d+)/(\\d+)[^\\d]+([\\d+:]+).*', '$3-$1-$2 $4') as timestamp) as modified, cast(regexp_replace(evaluated, '^(\\d+)/(\\d+)/(\\d+)[^\\d]+([\\d+:]+).*', '$3-$1-$2 $4') as timestamp) as evaluated, groups, ips, tags, agents, technologies, cast(controls as bigint) as controls, cast(assets as bigint) as assets, cast(instances as bigint) as instances, cast(regexp_replace(passed, '^(\\d+)\\s+\\(([\\d\\.]*)\\%\\)', '$1') as bigint) as passed, cast(regexp_replace(passed, '^(\\d+)\\s+\\(([\\d\\.]*)\\%\\)', '$2') as float) as passedperc, cast(regexp_replace(failures, '^(\\d+)\\s+\\(([\\d\\.]*)\\%\\)', '$1') as bigint) as failures, cast(regexp_replace(failures, '^(\\d+)\\s+\\(([\\d\\.]*)\\%\\)', '$2') as float) as failuresperc, cast(regexp_replace(error, '^(\\d+)\\s+\\(([\\d\\.]*)\\%\\)', '$1') as bigint) as error, cast(regexp_replace(error, '^(\\d+)\\s+\\(([\\d\\.]*)\\%\\)', '$2') as float) as errorperc, cast(exapproved as bigint) as exapproved, cast(expending as bigint) as expending, '${report.date}' as report_date FROM raw_pc_summary as pcs JOIN ( select name as n, max(report_date) as rd, max(report) as r from raw_pc_summary where datediff('${report.date}', report_date) between 0 and 6 group by name -- ) as q1 on (pcs.name=q1.n and pcs.report_date=q1.rd and pcs.report=q1.r) ) as q1 on (pcs.name=q1.n and pcs.report=q1.r) ;
INSERT OVERWRITE TABLE pc_controls PARTITION (report_date) SELECT translate(concat('${report.date}',report),'-','') as reportkey, cast(report as bigint) as report, ordering, cast(cid as bigint) as cid, deprecated, statement, criticalityname, cast(criticality as int) as criticality, cast(regexp_replace(percentage, '^([\\d\\.]+)[^\\d]+(\\d+)[^\\d]+(\\d+)[^\\d]+$', '$2') as bigint) as passed, cast(regexp_replace(percentage, '^([\\d\\.]+)[^\\d]+(\\d+)[^\\d]+(\\d+)[^\\d]+$', '$3') as bigint) as total, cast(regexp_replace(percentage, '^([\\d\\.]+)[^\\d]+(\\d+)[^\\d]+(\\d+)[^\\d]+$', '$1') as float) as percentage, '${report.date}' as report_date FROM raw_pc_controls as pcs JOIN ( select report as r, max(report_date) as rd from pc_summary where report_date='${report.date}' group by report ) as q1 on (pcs.report=cast(q1.r as string)) ;
INSERT OVERWRITE TABLE pc_hosts PARTITION (report_date) SELECT translate(concat('${report.date}',report),'-','') as reportkey, cast(report as bigint) as report, ip, tracking, dns, netbios, os, cast(regexp_replace(lastscan, '^(\\d+)/(\\d+)/(\\d+)[^\\d]+([\\d+:]+).*', '$3-$1-$2 $4') as timestamp) as lastscan, cast(regexp_replace(percentage, '^([\\d\\.]+)[^\\d]+(\\d+)[^\\d]+(\\d+)[^\\d]+$', '$2') as bigint) as passed, cast(regexp_replace(percentage, '^([\\d\\.]+)[^\\d]+(\\d+)[^\\d]+(\\d+)[^\\d]+$', '$3') as bigint) as total, cast(regexp_replace(percentage, '^([\\d\\.]+)[^\\d]+(\\d+)[^\\d]+(\\d+)[^\\d]+$', '$1') as float) as percentage, qghostid, '${report.date}' as report_date FROM raw_pc_hosts as pcs JOIN ( select report as r, max(report_date) as rd from pc_summary where report_date='${report.date}' group by report ) as q1 on (pcs.report=cast(q1.r as string)) ;
INSERT OVERWRITE TABLE pc_results PARTITION (report_date) SELECT translate(concat('${report.date}',report),'-','') as reportkey, cast(report as bigint) as report, ip, regexp_extract(dns, '^([^.]*).*$', 1) as dns, netbios, tracking, os, cpe, network, cast(regexp_replace(lastscan, '^(\\d+)/(\\d+)/(\\d+)[^\\d]+([\\d+:]+).*', '$3-$1-$2 $4') as timestamp) as lastscan, cast(regexp_replace(evaluation, '^(\\d+)/(\\d+)/(\\d+)[^\\d]+([\\d+:]+).*', '$3-$1-$2 $4') as timestamp) as evaluation, cast(cid as bigint) as cid, cref, technology, control, criticalityname, cast(criticality as int) as criticality, instance, rationale, status, remediation, deprecated, evidence, exassignee, exstatus, exenddate, excreator, excreated, exmodifier, exmodified, excomments, qghostid, '${report.date}' as report_date FROM raw_pc_results as pcs JOIN ( select report as r, max(report_date) as rd from pc_summary where report_date='${report.date}' group by report ) as q1 on (pcs.report=cast(q1.r as string)) WHERE instance not like 'Patter%' ;
INSERT OVERWRITE TABLE pc_tags PARTITION (report_date) SELECT translate(concat('${report.date}',report),'-','') as reportkey, cast(report as bigint) as report, ip, split(tags,',') as tags, '${report.date}' as report_date FROM raw_pc_tags as pcs JOIN ( select report as r, max(report_date) as rd from pc_summary where report_date='${report.date}' group by report ) as q1 on (pcs.report=cast(q1.r as string)) ;
