INSERT OVERWRITE TABLE pc_segment PARTITION (report_date) select translate(concat('${report.date}',report),'-','') as reportkey, cast(report as bigint) as report, pt.ip, st.title, st.segment, st.bu, '${report.date}' as report_date from tmp_pc_segment as pt left join ref_servertags as st on (translate(lower(pt.tag), ' ', '') = translate(lower(st.title), ' ', '')) JOIN ( select max(report_date) as rd from ref_servertags ) as q2 on (st.report_date=q2.rd) JOIN ( select report as r, max(report_date) as rd from pc_summary where report_date='${report.date}' group by report ) as q1 on (pt.report=cast(q1.r as string)) group by translate(concat('${report.date}',report),'-',''), report, pt.ip, st.title, st.segment, st.bu order by pt.ip, st.title, st.segment, st.bu ;
INSERT OVERWRITE TABLE pc_detail PARTITION (report_date) select md5(concat_ws(pr.reportkey, pt.segment, pt.bu, pr.qghostid, pr.ip, dns, netbios, platform, technology, instance)) as instancekey, pr.reportkey, pr.report, policy, pt.segment, pt.bu, concat_ws(',', sort_array(collect_set(pt.title))) as title, ps.name, pr.qghostid, pr.ip, dns, netbios, concat_ws(',', sort_array(collect_set(pr.tracking))) as tracking, concat_ws(',', sort_array(collect_set(pr.os))) as os, concat_ws(',', sort_array(collect_set(pr.cpe))) as cpe, max(cast(pr.lastscan as date)) as lastscan, max(cast(pr.evaluation as date)) as evaluation, platform, technology, instance, cid, control, criticalityname, cast(criticality as int) as criticality, status, evidence, '${report.date}' as report_date from pc_results as pr join pc_summary as ps on (pr.reportkey=ps.reportkey and ps.report_date=pr.report_date) join pc_segment as pt on (pr.reportkey=ps.reportkey and pr.ip=pt.ip and pt.report_date=pr.report_date) where pr.report_date='${report.date}' and instance not like 'Patter%' and policy not like '%RR5%' group by md5(concat_ws(pr.reportkey, pt.segment, pt.bu, pr.qghostid, pr.ip, dns, netbios, platform, technology, instance)), pr.reportkey, pr.report, policy, pt.segment, pt.bu, ps.name, pr.qghostid, pr.ip, dns, netbios, -- pr.os, pr.cpe, platform, technology, instance, cid, control, criticalityname, criticality, status, evidence, pr.report_date order by policy, pt.segment, pt.bu, pr.qghostid, pr.ip, platform, technology, instance, cid, control, criticalityname, criticality, status, evidence, pr.report ;
INSERT OVERWRITE TABLE pc_instance PARTITION (report_date) select md5(concat_ws(reportkey, segment, bu, qghostid, ip, dns, netbios, platform, technology, instance)) as instancekey, reportkey, report, policy, segment, bu, title, name, qghostid, ip, dns, netbios, tracking, os, cpe, max(cast(lastscan as date)) as lastscan, max(cast(evaluation as date)) as evaluation, platform, technology, instance, count(cid) as cids, sum(case when status='Passed' then 1 else 0 end) as passed, sum(case when status<>'Passed' then 1 else 0 end) as failed, (sum(case when status='Passed' then 1 else 0 end)*100)/count(cid) as percentage, '${report.date}' as report_date from pc_detail as pd where pd.report_date='${report.date}' group by md5(concat_ws(reportkey, segment, bu, qghostid, ip, dns, netbios, platform, technology, instance)), reportkey, report, policy, segment, bu, title, name, qghostid, ip, dns, netbios, tracking, os, cpe, platform, technology, instance, report_date order by report_date, policy, segment, ip, platform, technology, instance, report ;
INSERT OVERWRITE TABLE pc_authsummary PARTITION (report_date) select translate(concat('${report.date}',report),'-','') as reportkey, report, pcs.name, regexp_replace(pcs.name, '^PCAR-([^-]+)-([^-]+).*$', '$1') as segment, regexp_replace(pcs.name, '^PCAR-([^-]+)-([^-]+).*$', '$2') as policy, ag, cast(passed as bigint) as passed, cast(insufficient as bigint) as insufficient, cast(failures as bigint) as failures, cast(notattempted as bigint) as notattempted, cast(notinstalled as bigint) as notinstalled, cast(total as bigint) as total, cast(passedperc as float) as passedperc, cast(failedperc as float) as failedperc, cast(notattemptedperc as float) as notattemptedperc, '${report.date}' as report_date FROM raw_pc_authsummary as pcs join ( select name as n, max(report_date) as rd, max(report) as r from raw_pc_authsummary where datediff('${report.date}', report_date) between 0 and 1 group by name -- ) as pas on (pcs.report_date=pas.rd and pcs.name=pas.n and pcs.report=pas.r) ) as pas on (pcs.name=pas.n and pcs.report=pas.r) order by name ;
