INSERT OVERWRITE TABLE pc_auth PARTITION (report_date) select reportkey, report, name, segment, policy, ags, ip, dns, netbios, tracking, os, technology, hosttechnology, status, case when length(instance)>0 then instance else null end as instance, case when technology like 'MySQL' and reason like 'MySQL%database%' then regexp_replace(reason, '^.*\\s*\\s+database\\s+([^\\s,]+).*$', '$1') when technology like 'Oracle' and reason like '%on SID %' then regexp_replace(reason, '^.*\\s+SID\\s+([^\\s]+).*$', '$1') when technology like 'MSSQL' and reason like '%database%' then regexp_replace(reason, '^.*,\\s*database\\s*([^,]+).*$', '$1') when technology like 'MSSQL' and instance like '%Database Name%' then regexp_replace(instance, '^.*,\\s*Database Name=([^,]+).*$', '$1') when reason like 'DB2%DB%' then regexp_replace(reason, '^.*DB\\s*=\\s*([^,]+).*$', '$1') else null end as dbname, case when technology like 'Oracle' and reason like '%Service Name %' then regexp_replace(reason, '^.*\\s*Service Name\\s+([^\\s,]+).*$', '$1') when technology like 'MSSQL' and instance like '%Instance Name%' then regexp_replace(instance, '^.*Instance Name=([^,]+).*$', '$1') when technology like 'MSSQL' and reason like '%instance%' then regexp_replace(reason, '^.*instance\\s*([^,]+).*$', '$1') else null end as dbinstance, case when technology like 'Unix/Cisco/Checkpoint%' and reason like '%user%' then regexp_replace(reason, '^.*\\s*user=([^\\s,]+).*$', '$1') when technology like 'VMware%' and reason like '%user%' then regexp_replace(reason, '^.*\\s*user=([^\\s,]+).*$', '$1') when technology like 'Windows' and reason like '%user%' then regexp_replace(reason, '^.*\\s*user=([^\\s,]+).*$', '$1') when technology like 'MySQL' and reason like 'MySQL%for user%' then regexp_replace(reason, '^.*\\s*\\s+user\\s+\'([^\']+)\'@\'([^\']+).*$', '$1@$2') when technology like 'Oracle' and reason like '%for user%' then regexp_replace(reason, '^.*\\s*for user\\s+([^\\s]+).*$', '$1') when technology like 'MSSQL' and reason like '%user%' then regexp_replace(reason, '^.*user[\\s:\']*([^\\s,\']+).*$', '$1') when reason like 'DB2%user%' then regexp_replace(reason, '^.*user\\s*=\\s*([^,]+).*$', '$1') else null end as `user`, case when technology like 'Windows' and reason like '%domain%' then regexp_replace(reason, '^.*\\s*domain=([^\\s,]+).*$', '$1') else null end as `domain`, case when technology like 'MySQL' and reason like 'MySQL%port%' then regexp_replace(reason, '^.*\\s*\\s+port\\s+([\\d]+).*$', '$1') when technology like 'Oracle' and reason like '%port%' then regexp_replace(reason, '^.*,\\s*port\\s+([\\d.]+).*$', '$1') when technology like 'MSSQL' and reason like '%port%' then regexp_replace(reason, '^.*port[\\s=]+([\\d.]+).*$', '$1') when technology like 'MSSQL' and instance like '%Port%' then regexp_replace(instance, '^.*Port[\\s=]+([\\d.]+).*$', '$1') when reason like 'DB2%port%' then regexp_replace(reason, '^.*port\\s+(\\d+).*$', '$1') else null end as port, reason, lastauth, lastsuccess, '${report.date}' as report_date from ( select collect_set(par.reportkey) as reportkey, collect_set(par.report) as report, collect_set(pas.name) as name, regexp_replace(pas.name, '^PCAR-([^-]+)-([^-]+).*$', '$1') as segment, regexp_replace(pas.name, '^PCAR-([^-]+)-([^-]+).*$', '$2') as policy, technology, hosttechnology, ip, collect_set(dns) as dns, collect_set(netbios) as netbios, collect_set(tracking) as tracking, collect_set(os) as os, instance, status, reason, collect_set(par.ag) as ags, collect_set(lastauth) as lastauth, collect_set(lastsuccess) as lastsuccess, max(par.report_date) as report_date from pc_authresults as par join pc_authsummary as pas on (pas.report=par.report and pas.report_date='${report.date}') group by regexp_replace(pas.name, '^PCAR-([^-]+)-([^-]+).*$', '$1'), regexp_replace(pas.name, '^PCAR-([^-]+)-([^-]+).*$', '$2'), technology, hosttechnology, ip, instance, status, reason ) as pr ;

