from org.apache.nifi.processors.script import ExecuteScript from org.apache.nifi.processor.io import InputStreamCallback, OutputStreamCallback, StreamCallback from java.io import BufferedReader, BufferedWriter, InputStreamReader, OutputStreamWriter from java.nio.charset import StandardCharsets #import java.io import traceback
class subCallback(StreamCallback) : def __init__(self) : pass
def process(self, input, output) : try : self.reader = InputStreamReader(input) self.bufferedReader = BufferedReader(self.reader)
self.writer = OutputStreamWriter(output) self.bufferedWriter = BufferedWriter(self.writer)
l = 0 while 1 : line = self.bufferedReader.readLine() if line is None : break l += 1 self.bufferedWriter.write(line + "\n")
log.info("Read " + str(l) + " line(s)")
if self.bufferedReader is not None : #self.bufferedReader.flush() self.bufferedReader.close() if self.reader is not None : self.reader.close() if self.bufferedWriter is not None : #self.bufferedWriter.flush() self.bufferedWriter.close() if self.writer is not None : self.writer.close()
except : traceback.print_exc(file=sys.stdout) raise
