set tez.queue.name=ets; set hive.exec.dynamic.partition.mode=nonstrict; set hive.execution.engine = tez; set hive.vectorized.execution.enabled = true; set hive.vectorized.execution.reduce.enabled = true; set hive.auto.convert.join = true; set hive.cbo.enable = true; set hive.stats.autogather = true; set hive.compute.query.using.stats = true; set hive.stats.fetch.column.stats = false; set hive.optimize.ppd = true;
INSERT OVERWRITE TABLE pc_summary PARTITION (report_date) SELECT translate(concat(current_date,report),'-','') as reportkey, cast(report as bigint) as report, name, title, regexp_replace(name, '^PCAR-([^-]+)-([^-]+)-(.+)$', '$1') as segment, case when name like 'PCAR%' then regexp_replace(name, '^PCAR-([^-]+)-([^-]+)-(.+)\\s*$', '$2') when name like 'PAIR%' then regexp_replace(name, '^PAIR-([^-]*)-(.+)$', '$1') else '' end as policy, case when name like 'PCAR%' then regexp_replace(name, '^PCAR-([^-]+)-([^-]+)-(.+)$', '$3') when name like 'PAIR%' then regexp_replace(name, '^PAIR-([^-]*)-(.+)$', '$2') else '' end as platform, locking, cast(regexp_replace(modified, '^(\\d+)/(\\d+)/(\\d+)[^\\d]+([\\d+:]+).*', '$3-$1-$2 $4') as timestamp) as modified, cast(regexp_replace(evaluated, '^(\\d+)/(\\d+)/(\\d+)[^\\d]+([\\d+:]+).*', '$3-$1-$2 $4') as timestamp) as evaluated, groups, ips, tags, agents, technologies, cast(controls as bigint) as controls, cast(assets as bigint) as assets, cast(instances as bigint) as instances, cast(regexp_replace(passed, '^(\\d+)\\s+\\(([\\d\\.]*)\\%\\)', '$1') as bigint) as passed, cast(regexp_replace(passed, '^(\\d+)\\s+\\(([\\d\\.]*)\\%\\)', '$2') as float) as passedperc, cast(regexp_replace(failures, '^(\\d+)\\s+\\(([\\d\\.]*)\\%\\)', '$1') as bigint) as failures, cast(regexp_replace(failures, '^(\\d+)\\s+\\(([\\d\\.]*)\\%\\)', '$2') as float) as failuresperc, cast(regexp_replace(error, '^(\\d+)\\s+\\(([\\d\\.]*)\\%\\)', '$1') as bigint) as error, cast(regexp_replace(error, '^(\\d+)\\s+\\(([\\d\\.]*)\\%\\)', '$2') as float) as errorperc, cast(exapproved as bigint) as exapproved, cast(expending as bigint) as expending, current_date as report_date FROM raw_pc_summary as pcs JOIN ( select name as n, max(report_date) as rd from raw_pc_summary where datediff(current_date, report_date) between 0 and 7 group by name ) as q1 on (pcs.name=q1.n and pcs.report_date=q1.rd) ;
INSERT OVERWRITE TABLE pc_controls PARTITION (report_date) SELECT translate(concat(current_date,report),'-','') as reportkey, cast(report as bigint) as report, ordering, cast(cid as bigint) as cid, deprecated, statement, criticalityname, cast(criticality as int) as criticality, cast(regexp_replace(percentage, '^([\\d\\.]+)[^\\d]+(\\d+)[^\\d]+(\\d+)[^\\d]+$', '$2') as bigint) as passed, cast(regexp_replace(percentage, '^([\\d\\.]+)[^\\d]+(\\d+)[^\\d]+(\\d+)[^\\d]+$', '$3') as bigint) as total, cast(regexp_replace(percentage, '^([\\d\\.]+)[^\\d]+(\\d+)[^\\d]+(\\d+)[^\\d]+$', '$1') as float) as percentage, current_date as report_date FROM raw_pc_controls as pcs JOIN ( select report as r, max(report_date) as rd from pc_summary where report_date=current_date group by report ) as q1 on (pcs.report=cast(q1.r as string)) ;
INSERT OVERWRITE TABLE pc_hosts PARTITION (report_date) SELECT translate(concat(current_date,report),'-','') as reportkey, cast(report as bigint) as report, ip, tracking, dns, netbios, os, cast(regexp_replace(lastscan, '^(\\d+)/(\\d+)/(\\d+)[^\\d]+([\\d+:]+).*', '$3-$1-$2 $4') as timestamp) as lastscan, cast(regexp_replace(percentage, '^([\\d\\.]+)[^\\d]+(\\d+)[^\\d]+(\\d+)[^\\d]+$', '$2') as bigint) as passed, cast(regexp_replace(percentage, '^([\\d\\.]+)[^\\d]+(\\d+)[^\\d]+(\\d+)[^\\d]+$', '$3') as bigint) as total, cast(regexp_replace(percentage, '^([\\d\\.]+)[^\\d]+(\\d+)[^\\d]+(\\d+)[^\\d]+$', '$1') as float) as percentage, qghostid, current_date as report_date FROM raw_pc_hosts as pcs JOIN ( select report as r, max(report_date) as rd from pc_summary where report_date=current_date group by report ) as q1 on (pcs.report=cast(q1.r as string)) ;
INSERT OVERWRITE TABLE pc_results PARTITION (report_date) SELECT translate(concat(current_date,report),'-','') as reportkey, cast(report as bigint) as report, ip, dns, netbios, tracking, os, cpe, network, cast(regexp_replace(lastscan, '^(\\d+)/(\\d+)/(\\d+)[^\\d]+([\\d+:]+).*', '$3-$1-$2 $4') as timestamp) as lastscan, cast(regexp_replace(evaluation, '^(\\d+)/(\\d+)/(\\d+)[^\\d]+([\\d+:]+).*', '$3-$1-$2 $4') as timestamp) as evaluation, cast(cid as bigint) as cid, cref, technology, control, criticalityname, cast(criticality as int) as criticality, instance, rationale, status, remediation, deprecated, evidence, exassignee, exstatus, exenddate, excreator, excreated, exmodifier, exmodified, excomments, qghostid, current_date as report_date FROM raw_pc_results as pcs JOIN ( select report as r, max(report_date) as rd from pc_summary where report_date=current_date group by report ) as q1 on (pcs.report=cast(q1.r as string)) WHERE instance not like 'Patter%' ;
INSERT OVERWRITE TABLE pc_tags PARTITION (report_date) SELECT translate(concat(current_date,report),'-','') as reportkey, cast(report as bigint) as report, ip, split(tags,',') as tags, current_date as report_date FROM raw_pc_tags as pcs JOIN ( select report as r, max(report_date) as rd from pc_summary where report_date=current_date group by report ) as q1 on (pcs.report=cast(q1.r as string)) ;
INSERT OVERWRITE TABLE pc_segment PARTITION (report_date) select translate(concat(current_date,report),'-','') as reportkey, cast(report as bigint) as report, pt.ip, st.title, st.segment, st.bu, current_date as report_date from pc_tags as pt left join ref_servertags as st on (pt.report_date=st.report_date) JOIN ( select reportkey, max(report_date) as rd from pc_summary where report_date=current_date group by reportkey ) as q1 on (pt.reportkey=q1.reportkey) where array_contains(pt.tags, st.title) group by translate(concat(current_date,report),'-',''), report, pt.ip, st.title, st.segment, st.bu order by pt.ip, st.title, st.segment, st.bu ;
INSERT OVERWRITE TABLE pc_detail PARTITION (report_date) select md5(concat_ws(pr.reportkey, pt.segment, pt.bu, pr.qghostid, pr.ip, dns, netbios, platform, technology, instance)) as instancekey, pr.reportkey, pr.report, policy, pt.segment, pt.bu, collect_set(pt.title) as title, ps.name, pr.qghostid, pr.ip, dns, netbios, platform, technology, instance, cid, control, status, evidence, pr.report_date from pc_results as pr join pc_summary as ps on (pr.reportkey=ps.reportkey) join pc_segment as pt on (pr.reportkey=ps.reportkey and pr.ip=pt.ip) where pr.report_date in (select max(report_date) from pc_summary) and instance not like 'Patter%' and policy not like '%RR5%' group by md5(concat_ws(pr.reportkey, pt.segment, pt.bu, pr.qghostid, pr.ip, dns, netbios, platform, technology, instance)), pr.reportkey, pr.report, policy, pt.segment, pt.bu, ps.name, pr.qghostid, pr.ip, dns, netbios, platform, technology, instance, cid, control, status, evidence, pr.report_date order by pr.report_date, policy, pt.segment, pt.bu, pr.qghostid, pr.ip, platform, technology, instance, cid, control, status, evidence, pr.report ;
INSERT OVERWRITE TABLE pc_instance PARTITION (report_date) select md5(concat_ws(reportkey, segment, bu, qghostid, ip, dns, netbios, platform, technology, instance)) as instancekey, reportkey, report, policy, segment, bu, title, name, qghostid, ip, dns, netbios, platform, technology, instance, count(cid) as cids, sum(case when status='Passed' then 1 else 0 end) as passed, sum(case when status<>'Passed' then 1 else 0 end) as failed, (sum(case when status='Passed' then 1 else 0 end)*100)/count(cid) as percentage, report_date from pc_detail where report_date in (select max(report_date) from pc_detail) group by md5(concat_ws(reportkey, segment, bu, qghostid, ip, dns, netbios, platform, technology, instance)), reportkey, report, policy, segment, bu, title, name, qghostid, ip, dns, netbios, platform, technology, instance, report_date order by report_date, policy, segment, ip, platform, technology, instance, report ;
INSERT OVERWRITE TABLE pc_authsummary PARTITION (report_date) select translate(concat(current_date,report),'-','') as reportkey, report, pcs.name, regexp_replace(pcs.name, '^PCAR-([^-]+)-([^-]+).*$', '$1') as segment, regexp_replace(pcs.name, '^PCAR-([^-]+)-([^-]+).*$', '$2') as policy, ag, cast(passed as bigint) as passed, cast(insufficient as bigint) as insufficient, cast(failures as bigint) as failures, cast(notattempted as bigint) as notattempted, cast(notinstalled as bigint) as notinstalled, cast(total as bigint) as total, cast(passedperc as float) as passedperc, cast(failedperc as float) as failedperc, cast(notattemptedperc as float) as notattemptedperc, current_date as report_date FROM raw_pc_authsummary as pcs join (select name as n, max(report_date) as rd from raw_pc_authsummary where datediff(current_date, report_date) between 0 and 1 group by name) as pas on (pcs.report_date=pas.rd and pcs.name=pas.n) order by name ;
INSERT OVERWRITE TABLE pc_authresults PARTITION (report_date) select translate(concat(current_date,par.report),'-','') as reportkey, cast(par.report as bigint) as report, par.ag as ag, technology, hosttechnology, instance, ip, dns, netbios, tracking, network, status, reason, os, cast(regexp_replace(lastauth, '^(\\d+)/(\\d+)/(\\d+).*', '$3-$1-$2') as date) as lastauth, cast(regexp_replace(lastsuccess, '^(\\d+)/(\\d+)/(\\d+).*', '$3-$1-$2') as date) as lastsuccess, current_date as report_date from raw_pc_authresults as par join pc_authsummary as pas on (pas.report=par.report and pas.report_date=current_date) ;
INSERT OVERWRITE TABLE pc_authappendix PARTITION (report_date) select translate(concat(current_date,paa.report),'-','') as reportkey, cast(paa.report as bigint) as report, ip, dns, netbios, tracking, network, os, cast(regexp_replace(lastauth, '^(\\d+)/(\\d+)/(\\d+).*', '$3-$1-$2') as date) as lastauth, cast(regexp_replace(lastsuccess, '^(\\d+)/(\\d+)/(\\d+).*', '$3-$1-$2') as date) as lastsuccess, hosttechnology, instance, current_date as report_date from raw_pc_authappendix as paa join pc_authsummary as pas on (pas.report=paa.report and pas.report_date=current_date) ;
INSERT OVERWRITE TABLE pc_auth PARTITION (report_date) select reportkey, report, name, segment, policy, ags, ip, dns, netbios, tracking, os, technology, hosttechnology, status, case when length(instance)>0 then instance else null end as instance, case when technology like 'MySQL' and reason like 'MySQL%database%' then regexp_replace(reason, '^.*\\s*\\s+database\\s+([^\\s,]+).*$', '$1') when technology like 'Oracle' and reason like '%on SID %' then regexp_replace(reason, '^.*\\s+SID\\s+([^\\s]+).*$', '$1') when technology like 'MSSQL' and reason like '%database%' then regexp_replace(reason, '^.*,\\s*database\\s*([^,]+).*$', '$1') when technology like 'MSSQL' and instance like '%Database Name%' then regexp_replace(instance, '^.*,\\s*Database Name=([^,]+).*$', '$1') when reason like 'DB2%DB%' then regexp_replace(reason, '^.*DB\\s*=\\s*([^,]+).*$', '$1') else null end as dbname, case when technology like 'Oracle' and reason like '%Service Name %' then regexp_replace(reason, '^.*\\s*Service Name\\s+([^\\s,]+).*$', '$1') when technology like 'MSSQL' and instance like '%Instance Name%' then regexp_replace(instance, '^.*Instance Name=([^,]+).*$', '$1') when technology like 'MSSQL' and reason like '%instance%' then regexp_replace(reason, '^.*instance\\s*([^,]+).*$', '$1') else null end as dbinstance, case when technology like 'Unix/Cisco/Checkpoint%' and reason like '%user%' then regexp_replace(reason, '^.*\\s*user=([^\\s,]+).*$', '$1') when technology like 'VMware%' and reason like '%user%' then regexp_replace(reason, '^.*\\s*user=([^\\s,]+).*$', '$1') when technology like 'Windows' and reason like '%user%' then regexp_replace(reason, '^.*\\s*user=([^\\s,]+).*$', '$1') when technology like 'MySQL' and reason like 'MySQL%for user%' then regexp_replace(reason, '^.*\\s*\\s+user\\s+\'([^\']+)\'@\'([^\']+).*$', '$1@$2') when technology like 'Oracle' and reason like '%for user%' then regexp_replace(reason, '^.*\\s*for user\\s+([^\\s]+).*$', '$1') when technology like 'MSSQL' and reason like '%user%' then regexp_replace(reason, '^.*user[\\s:\']*([^\\s,\']+).*$', '$1') when reason like 'DB2%user%' then regexp_replace(reason, '^.*user\\s*=\\s*([^,]+).*$', '$1') else null end as `user`, case when technology like 'Windows' and reason like '%domain%' then regexp_replace(reason, '^.*\\s*domain=([^\\s,]+).*$', '$1') else null end as `domain`, case when technology like 'MySQL' and reason like 'MySQL%port%' then regexp_replace(reason, '^.*\\s*\\s+port\\s+([\\d]+).*$', '$1') when technology like 'Oracle' and reason like '%port%' then regexp_replace(reason, '^.*,\\s*port\\s+([\\d.]+).*$', '$1') when technology like 'MSSQL' and reason like '%port%' then regexp_replace(reason, '^.*port[\\s=]+([\\d.]+).*$', '$1') when technology like 'MSSQL' and instance like '%Port%' then regexp_replace(instance, '^.*Port[\\s=]+([\\d.]+).*$', '$1') when reason like 'DB2%port%' then regexp_replace(reason, '^.*port\\s+(\\d+).*$', '$1') else null end as port, reason, lastauth, lastsuccess, current_date as report_date from ( select collect_set(par.reportkey) as reportkey, collect_set(par.report) as report, collect_set(pas.name) as name, regexp_replace(pas.name, '^PCAR-([^-]+)-([^-]+).*$', '$1') as segment, regexp_replace(pas.name, '^PCAR-([^-]+)-([^-]+).*$', '$2') as policy, technology, hosttechnology, ip, collect_set(dns) as dns, collect_set(netbios) as netbios, collect_set(tracking) as tracking, collect_set(os) as os, instance, status, reason, collect_set(par.ag) as ags, collect_set(lastauth) as lastauth, collect_set(lastsuccess) as lastsuccess, max(par.report_date) as report_date from pc_authresults as par join pc_authsummary as pas on (pas.report=par.report and pas.report_date=current_date) group by regexp_replace(pas.name, '^PCAR-([^-]+)-([^-]+).*$', '$1'), regexp_replace(pas.name, '^PCAR-([^-]+)-([^-]+).*$', '$2'), technology, hosttechnology, ip, instance, status, reason ) as pr ;

