entry.value:
set tez.queue.name=ets; set hive.exec.dynamic.partition.mode=nonstrict;
set hive.execution.engine = tez; set hive.vectorized.execution.enabled = true; set hive.vectorized.execution.reduce.enabled = true; set hive.auto.convert.join = true; set hive.cbo.enable = true; set hive.stats.autogather = true; set hive.compute.query.using.stats = true; set hive.stats.fetch.column.stats = false; set hive.optimize.ppd = true;
INSERT OVERWRITE TABLE typed_hosts PARTITION (report_date) SELECT cast(id as INT) as id, qghostid, ip, cast(regexp_extract(ip, '^(\\d+)\\.\\d+\\.\\d+\\.\\d+', 1) as BIGINT) * 16777216 + cast(regexp_extract(ip, '^\\d+\\.(\\d+)\\.\\d+\\.\\d+', 1) as BIGINT) * 65536 + cast(regexp_extract(ip, '^\\d+\\.\\d+\\.(\\d+)\\.\\d+', 1) as BIGINT) * 256 + cast(regexp_extract(ip, '^\\d+\\.\\d+\\.\\d+\\.(\\d+)', 1) as BIGINT) as dip, dns, netbios, os, cpe, tracking, cast(network as INT) as network, cast(trim(translate(translate(lastscan, 'T', ' '), 'Z', ' ')) as TIMESTAMP) as lastscan, cast(trim(translate(translate(lastvmscan, 'T', ' '), 'Z', ' ')) as TIMESTAMP) as lastvmscan, cast(trim(translate(translate(lastvmauth, 'T', ' '), 'Z', ' ')) as TIMESTAMP) as lastvmauth, cast(trim(translate(translate(lastpcscan, 'T', ' '), 'Z', ' ')) as TIMESTAMP) as lastpcscan, report_date FROM raw_hosts where report_date = ${report_date} ;
INSERT OVERWRITE TABLE typed_hosttags PARTITION (report_date) SELECT cast(id as INT) as id, cast(tagid as INT) as tagid, name, report_date FROM raw_hosttags where report_date = ${report_date} ;
INSERT OVERWRITE TABLE typed_vm PARTITION (report_date) SELECT cast(id as INT) as id, cast(qid as INT) as qid, status, type, cast(severity as INT) as severity, results, cast(port as INT) as port, protocol, service, cast(ssl as INT) as ssl, cast(disabled as INT) as disabled, cast(ignored as INT) as ignored, cast(arfkernel as INT) as arfkernel, cast(arfservice as INT) as arfservice, cast(arfconfig as INT) as arfconfig, cast(timesfound as INT) as timesfound, cast(trim(translate(translate(firstfound, 'T', ' '), 'Z', ' ')) as TIMESTAMP) as firstfound, cast(trim(translate(translate(lastfound, 'T', ' '), 'Z', ' ')) as TIMESTAMP) as lastfound, cast(trim(translate(translate(lastprocessed, 'T', ' '), 'Z', ' ')) as TIMESTAMP) as lastprocessed, report_date FROM raw_vm where report_date = ${report_date} ;
INSERT OVERWRITE TABLE typed_kb PARTITION (report_date) SELECT cast(qid as INT) as qid, title, type, cast(severity as INT) as severity, category, discovery, patchable, pci, diagnosis, consequence, solution, cast(trim(translate(translate(lastmodified, 'T', ' '), 'Z', ' ')) as TIMESTAMP) as lastmodified, cast(trim(translate(translate(published, 'T', ' '), 'Z', ' ')) as TIMESTAMP) as published, report_date FROM raw_kb where report_date = ${report_date} ;
INSERT OVERWRITE TABLE typed_maptbl PARTITION (report_date) SELECT cast(id as INT) as id, type, rid, rurl, report_date FROM raw_maptbl where report_date = ${report_date} ;
INSERT OVERWRITE TABLE typed_cvss PARTITION (report_date) SELECT cast(qid as INT) as qid, cast(base as DOUBLE) as base, cast(temporal as DOUBLE) as temporal, cast(vector as DOUBLE) as vector, cast(accessVector as DOUBLE) as accessVector, cast(accessComplexity as DOUBLE) as accessComplexity, cast(impactConfidentiality as DOUBLE) as impactConfidentiality, cast(impactIntegrity as DOUBLE) as impactIntegrity, cast(impactAvailability as DOUBLE) as impactAvailability, cast(authentication as DOUBLE) as authentication, cast(exploitability as DOUBLE) as exploitability, cast(remediationLevel as DOUBLE) as remediationLevel, cast(reportConfidence as DOUBLE) as reportConfidence, report_date FROM raw_cvss where report_date = ${report_date} ;
INSERT OVERWRITE TABLE typed_ags PARTITION (report_date) SELECT cast(id as INT) as id, title, cast(ownerid as INT) as ownerid, ownername, cast(network as INT) as network, cast(trim(translate(translate(lastupdate, 'T', ' '), 'Z', ' ')) as TIMESTAMP) as lastupdate, businessimpact, cvssCDP, cvssTD, cvssCR, cvssIR, cvssAR, ipset, unitids, report_date FROM raw_ags where report_date = ${report_date} ;
INSERT OVERWRITE TABLE typed_tags PARTITION (report_date) SELECT cast(id as INT) as id, title, cast(trim(translate(translate(created, 'T', ' '), 'Z', ' ')) as TIMESTAMP) as created, cast(trim(translate(translate(modified, 'T', ' '), 'Z', ' ')) as TIMESTAMP) as modified, ruletext, ruletype, ag, businessunit, color, cast(parent as INT) as parent, children, report_date FROM raw_tags where report_date = ${report_date} and cast(id as INT) is not NULL and children rlike '^[\\d,-]*$' group by id, title, created, modified, ruletext, ruletype, ag, businessunit, color, parent, children, report_date ;
INSERT OVERWRITE TABLE typed_sl PARTITION (report_date) SELECT cast(id as INT) as id, title, type, owner, global, split(qids,',') as qids, criteria, cast(trim(translate(translate(created, 'T', ' '), 'Z', ' ')) as TIMESTAMP) as created, cast(trim(translate(translate(modified, 'T', ' '), 'Z', ' ')) as TIMESTAMP) as modified, modifiedby, report_date FROM raw_sl where report_date = ${report_date} ;
DROP TABLE IF EXISTS temp_hosts; CREATE TEMPORARY TABLE temp_hosts AS SELECT cast(id as string) as id, concat_ws('|',collect_set(qghostid)) as qghostid, concat_ws('|',collect_set(ip)) as ip, concat_ws('|',collect_set(cast(dip as STRING))) as dip, concat_ws('|',collect_set(dns)) as dns, -- concat_ws('|',collect_set(regexp_replace(dns, '^(([^\\.]+).*|(.+))$', '$2'))) as dns, concat_ws('|',collect_set(netbios)) as netbios, concat_ws('|',collect_set(os)) as os, concat_ws('|',collect_set(cpe)) as cpe, concat_ws('|',collect_set(tracking)) as tracking, concat_ws('|',collect_set(cast(network as string))) as network, max(lastscan) as lastscan, max(lastvmscan) as lastvmscan, max(lastvmauth) as lastvmauth, max(lastpcscan) as lastpcscan, max(report_date) as report_date FROM typed_hosts as n1 where report_date = ${report_date} group by id ;
DROP TABLE IF EXISTS temp_hosts2; CREATE TEMPORARY TABLE temp_hosts2 AS SELECT concat_ws('|',collect_set(id)) as id, qghostid, -- concat_ws('|',collect_set(qghostid)) as qghostid, concat_ws('|',collect_set(ip)) as ip, concat_ws('|',collect_set(dip)) as dip, concat_ws('|',collect_set(dns)) as dns, concat_ws('|',collect_set(netbios)) as netbios, concat_ws('|',collect_set(os)) as os, concat_ws('|',collect_set(cpe)) as cpe, concat_ws('|',collect_set(tracking)) as tracking, concat_ws('|',collect_set(cast(network as string))) as network, max(lastscan) as lastscan, max(lastvmscan) as lastvmscan, max(lastvmauth) as lastvmauth, max(lastpcscan) as lastpcscan, max(report_date) as report_date FROM temp_hosts as n1 where report_date = ${report_date} AND length(trim(qghostid)) > 0 group by qghostid UNION SELECT id, qghostid, ip, dip, dns, netbios, os, cpe, tracking, network, lastscan, lastvmscan, lastvmauth, lastpcscan, report_date from temp_hosts where report_date = ${report_date} AND length(trim(qghostid)) = 0 ;
DROP TABLE IF EXISTS temp_hosts3; CREATE TEMPORARY TABLE temp_hosts3 AS SELECT concat_ws('|',collect_set(id)) as id, concat_ws('|',collect_set(qghostid)) as qghostid, concat_ws('|',collect_set(ip)) as ip, concat_ws('|',collect_set(dip)) as dip, concat_ws('|',collect_set(dns)) as dns, netbios, -- concat_ws('|',collect_set(netbios)) as netbios, concat_ws('|',collect_set(os)) as os, concat_ws('|',collect_set(cpe)) as cpe, concat_ws('|',collect_set(tracking)) as tracking, concat_ws('|',collect_set(cast(network as string))) as network, max(lastscan) as lastscan, max(lastvmscan) as lastvmscan, max(lastvmauth) as lastvmauth, max(lastpcscan) as lastpcscan, max(report_date) as report_date FROM temp_hosts2 as n1 where report_date = ${report_date} AND length(trim(netbios)) > 0 group by netbios UNION SELECT id, qghostid, ip, dip, dns, netbios, os, cpe, tracking, network, lastscan, lastvmscan, lastvmauth, lastpcscan, report_date from temp_hosts2 where report_date = ${report_date} AND length(trim(netbios)) = 0 ;
DROP TABLE IF EXISTS temp_hosts4; CREATE TEMPORARY TABLE temp_hosts4 AS SELECT concat_ws('|',collect_set(id)) as id, concat_ws('|',collect_set(qghostid)) as qghostid, concat_ws('|',collect_set(ip)) as ip, concat_ws('|',collect_set(dip)) as dip, dns, -- concat_ws('|',collect_set(dns)) as dns, concat_ws('|',collect_set(netbios)) as netbios, concat_ws('|',collect_set(os)) as os, concat_ws('|',collect_set(cpe)) as cpe, concat_ws('|',collect_set(tracking)) as tracking, concat_ws('|',collect_set(cast(network as string))) as network, max(lastscan) as lastscan, max(lastvmscan) as lastvmscan, max(lastvmauth) as lastvmauth, max(lastpcscan) as lastpcscan, max(report_date) as report_date FROM temp_hosts3 as n1 where report_date = ${report_date} AND length(trim(dns)) > 0 group by dns UNION SELECT id, qghostid, ip, dip, dns, netbios, os, cpe, tracking, network, lastscan, lastvmscan, lastvmauth, lastpcscan, report_date from temp_hosts3 where report_date = ${report_date} AND length(trim(dns)) = 0 ;
INSERT OVERWRITE TABLE unified PARTITION (report_date) SELECT split(id, '\\|') as id, split(qghostid, '\\|') as qghostid, split(ip, '\\|') as ip, split(dip, '\\|') as dip, split(dns, '\\|') as dns, split(netbios, '\\|') as netbios, split(os, '\\|') as os, split(cpe, '\\|') as cpe, split(tracking, '\\|') as tracking, split(network, '\\|') as network, lastscan, lastvmscan, lastvmauth, lastpcscan, report_date from temp_hosts4 where report_date = ${report_date} ;
INSERT OVERWRITE TABLE final_hosts PARTITION (report_date) SELECT translate(concat(report_date,id[0]),'-','') as parentkey, translate(concat(report_date,a1),'-','') as hostkey, id[0] as parentid, a1 as id, qghostid[0] as qghostid, ip[0] as ip, cast(dip[0] as INT) as dip, dns[0] as dns, netbios[0] as netbios, os[0] as os, cpe[0] as cpe, tracking[0] as tracking, network[0] as network, lastscan, lastvmscan, lastvmauth, lastpcscan, report_date FROM unified LATERAL VIEW explode(id) a as a1 where report_date=${report_date} order by parentid, id ;
INSERT OVERWRITE TABLE final_hosttags PARTITION (report_date) SELECT translate(concat(report_date,id),'-','') as hostkey, translate(concat(report_date,tagid),'-','') as tagkey, id, tagid, name, report_date FROM typed_hosttags where report_date = ${report_date} group by translate(concat(report_date,id),'-',''), translate(concat(report_date,tagid),'-',''), id,
tagid, name, report_date ;
INSERT OVERWRITE TABLE final_vm PARTITION (report_date) SELECT translate(concat(report_date,id),'-','') as hostkey, translate(concat(report_date,qid),'-','') as qidkey, id, qid, status, type, severity, results, port, protocol, service, ssl, disabled, ignored, arfkernel, arfservice, arfconfig, timesfound, firstfound, lastfound, lastprocessed, report_date FROM typed_vm where report_date = ${report_date} ;
INSERT OVERWRITE TABLE final_kb PARTITION (report_date) SELECT translate(concat(report_date,qid),'-','') as qidkey, qid, title, type, severity, category, discovery, patchable, pci, diagnosis, consequence, solution, lastmodified, published, report_date FROM typed_kb where report_date = ${report_date} ;
INSERT OVERWRITE TABLE final_maptbl PARTITION (report_date) SELECT translate(concat(report_date,id),'-','') as qidkey, id, type, rid, rurl, report_date FROM typed_maptbl where report_date = ${report_date} ;
INSERT OVERWRITE TABLE final_cvss PARTITION (report_date) SELECT translate(concat(report_date,qid),'-','') as qidkey, qid, base, temporal, vector, accessVector, accessComplexity, impactConfidentiality, impactIntegrity, impactAvailability, authentication, exploitability, remediationLevel, reportConfidence, report_date FROM typed_cvss where report_date = ${report_date} ;
INSERT OVERWRITE TABLE final_ags PARTITION (report_date) SELECT translate(concat(report_date,id),'-','') as agskey, id, title, ownerid, ownername, network, lastupdate, businessimpact, cvssCDP, cvssTD, cvssCR, cvssIR, cvssAR, ipset, unitids, report_date FROM typed_ags where report_date = ${report_date} ;
DROP TABLE IF EXISTS temp_ags_1; CREATE TEMPORARY TABLE temp_ags_1 AS SELECT id, split(regexp_replace(ipset, '^,(.*),$', '$1'), ',') as ipset, report_date FROM typed_ags where report_date = ${report_date} ;
DROP TABLE IF EXISTS temp_ags_2; CREATE TEMPORARY TABLE temp_ags_2 AS SELECT id, split(a1, '-') as ips, report_date FROM temp_ags_1 LATERAL VIEW explode(ipset) a as a1 where report_date = ${report_date} ;
DROP TABLE IF EXISTS temp_ags_3; CREATE TEMPORARY TABLE temp_ags_3 AS SELECT id, ips, cast(regexp_extract(ips[0], '^(\\d+)\\.\\d+\\.\\d+\\.\\d+', 1) as BIGINT) * 16777216 + cast(regexp_extract(ips[0], '^\\d+\\.(\\d+)\\.\\d+\\.\\d+', 1) as BIGINT) * 65536 + cast(regexp_extract(ips[0], '^\\d+\\.\\d+\\.(\\d+)\\.\\d+', 1) as BIGINT) * 256 + cast(regexp_extract(ips[0], '^\\d+\\.\\d+\\.\\d+\\.(\\d+)', 1) as BIGINT) as bdip, case when size(ips) > 1 then cast(regexp_extract(ips[1], '^(\\d+)\\.\\d+\\.\\d+\\.\\d+', 1) as BIGINT) * 16777216 + cast(regexp_extract(ips[1], '^\\d+\\.(\\d+)\\.\\d+\\.\\d+', 1) as BIGINT) * 65536 + cast(regexp_extract(ips[1], '^\\d+\\.\\d+\\.(\\d+)\\.\\d+', 1) as BIGINT) * 256 + cast(regexp_extract(ips[1], '^\\d+\\.\\d+\\.\\d+\\.(\\d+)', 1) as BIGINT) else cast(regexp_extract(ips[0], '^(\\d+)\\.\\d+\\.\\d+\\.\\d+', 1) as BIGINT) * 16777216 + cast(regexp_extract(ips[0], '^\\d+\\.(\\d+)\\.\\d+\\.\\d+', 1) as BIGINT) * 65536 + cast(regexp_extract(ips[0], '^\\d+\\.\\d+\\.(\\d+)\\.\\d+', 1) as BIGINT) * 256 + cast(regexp_extract(ips[0], '^\\d+\\.\\d+\\.\\d+\\.(\\d+)', 1) as BIGINT) end as edip, report_date FROM temp_ags_2 -- LATERAL VIEW explode(ips) a as ip where report_date = ${report_date} ;
INSERT OVERWRITE TABLE final_agsdip PARTITION (report_date) SELECT translate(concat(report_date,id),'-','') as agskey, id, ips, bdip, edip, report_date FROM temp_ags_3 where report_date = ${report_date} ;
