INSERT OVERWRITE TABLE final_tags PARTITION (report_date) SELECT translate(concat(report_date,parent),'-','') as parentkey, translate(concat(report_date,id),'-','') as tagkey, id, title, created, modified, ruletext, ruletype, ag, businessunit, color, parent, children, report_date FROM typed_tags where report_date = ${report_date} ;
------------------------
INSERT OVERWRITE TABLE final_sl PARTITION (report_date) SELECT translate(concat(report_date,id),'-','') as slkey, translate(concat(report_date,a1),'-','') as qidkey, cast(id as INT) as id, title, type, owner, global, a1 as qid, criteria, created, modified, modifiedby, report_date FROM typed_sl LATERAL VIEW explode(qids) a as a1 where report_date = ${report_date} ;
