INSERT OVERWRITE TABLE ref_vendor PARTITION (report_date) SELECT translate(concat(report_date,id),'-','') as qidkey, cast(id as INT) as id, cast(severity as INT) as severity, application, vendor, report_date FROM raw_vendor WHERE report_date = ${report_date} AND id is not NULL ;
---------------------------
INSERT OVERWRITE TABLE ref_bumappings PARTITION (report_date) SELECT case when type not like 'AT' and t not like 'AT' then translate(concat(report_date,a1.key),'-','') else '' end as agskey, case when type like 'AT' and t like 'AT' then translate(concat(report_date,a1.key),'-','') else '' end as tagkey, bu, title, type, purpose, owner, secondary, report_date FROM raw_bumappings as a0 LEFT JOIN ( SELECT 'AG' as t, agskey as key, title as name FROM final_ags WHERE report_date = ${report_date} UNION SELECT 'AT' as t, tagkey as key, title as name FROM final_tags WHERE report_date = ${report_date} ORDER BY name ) as a1 ON (a0.type = a1.t AND a0.title = a1.name) WHERE report_date = ${report_date} AND bu is not NULL ORDER BY title ;
--------------------
INSERT OVERWRITE TABLE ref_servertags PARTITION (report_date) SELECT key as tagkey, title, segment, bu, report_date FROM raw_servertags as a0 LEFT JOIN ( SELECT tagkey as key, title as name FROM final_tags WHERE report_date = ${report_date} ) as a1 ON (lower(translate(a0.title, ' ', '')) = lower(translate(a1.name, ' ', ''))) WHERE report_date = ${report_date} AND title is not NULL AND length(translate(title, ' ', '')) > 0 AND title <> "TAG DESC" ORDER BY title ;
--------------------
INSERT OVERWRITE TABLE ref_desktoptags PARTITION (report_date) SELECT a1.key as parentkey, a2.key as childkey, segment, parent, child, ctags, report_date FROM raw_desktoptags as a0 LEFT JOIN ( SELECT tagkey as key, title as name FROM final_tags WHERE report_date = ${report_date} ) as a1 ON (lower(trim(a0.parent)) = lower(trim(a1.name))) LEFT JOIN ( SELECT tagkey as key, title as name FROM final_tags WHERE report_date = ${report_date} ) as a2 ON (lower(trim(a0.child)) = lower(trim(a2.name))) WHERE report_date = ${report_date} AND segment is not NULL ;
-- break out desktoptag family tree and all children from the desktoptag parents
drop table if exists temp_desktoptags; create temporary table temp_desktoptags as select d1.parentkey, d1.parent, t1.tagkey, t1.title, 1 as depth, t1.report_date from final_tags as t1 join ref_desktoptags as d1 on (d1.parentkey = t1.tagkey) where t1.report_date=${report_date} ;
drop table if exists temp_desktoptags2; create temporary table temp_desktoptags2 as select parentkey, parent, tagkey, title, depth as depth, report_date from temp_desktoptags union select d1.parentkey, d1.parent, t1.tagkey, t1.title, depth+1 as depth, t1.report_date from final_tags as t1 join temp_desktoptags as d1 on (d1.tagkey = t1.parentkey) ;
drop table if exists temp_desktoptags3; create temporary table temp_desktoptags3 as select parentkey, parent, tagkey, title, depth as depth, report_date from temp_desktoptags2 union select d1.parentkey, d1.parent, t1.tagkey, t1.title, depth+1 as depth, t1.report_date from final_tags as t1 join temp_desktoptags2 as d1 on (d1.tagkey = t1.parentkey) ;
drop table if exists temp_desktoptags4; create temporary table temp_desktoptags4 as select parentkey, parent, tagkey, title, depth as depth, report_date from temp_desktoptags3 union select d1.parentkey, d1.parent, t1.tagkey, t1.title, depth+1 as depth, t1.report_date from final_tags as t1 join temp_desktoptags3 as d1 on (d1.tagkey = t1.parentkey) ;
drop table if exists temp_desktoptags5; create temporary table temp_desktoptags5 as select parentkey, parent, tagkey, title, depth as depth, report_date from temp_desktoptags4 union select d1.parentkey, d1.parent, t1.tagkey, t1.title, depth+1 as depth, t1.report_date from final_tags as t1 join temp_desktoptags4 as d1 on (d1.tagkey = t1.parentkey) ;
drop table if exists temp_desktoptags6; create temporary table temp_desktoptags6 as select parentkey, parent, tagkey, title, depth as depth, report_date from temp_desktoptags5 union select d1.parentkey, d1.parent, t1.tagkey, t1.title, depth+1 as depth, t1.report_date from final_tags as t1 join temp_desktoptags5 as d1 on (d1.tagkey = t1.parentkey) ;
INSERT OVERWRITE TABLE ref_desktopchild PARTITION (report_date) select parentkey, parent, tagkey, title, depth as depth, report_date from temp_desktoptags6 union select d1.parentkey, d1.parent, t1.tagkey, t1.title, depth+1 as depth, t1.report_date from final_tags as t1 join temp_desktoptags6 as d1 on (d1.tagkey = t1.parentkey) ;
