INSERT OVERWRITE TABLE ref_patch PARTITION (report_date) SELECT translate(concat(report_date,qid),'-','') as qidkey, cast(qid as INT), concat_ws(',', collect_set(vendorid)) as vendorid, max(severity), title, cast(max(hostcount) as INT) as hostcount, case when published rlike '^(\\d+)-(\\d+)-(\\d+)\\s*(\\d+)\\s*.*$' then regexp_replace(published, '^(\\d+)-(\\d+)-(\\d+)\\s*.*$', '$1-$2-$3') else regexp_replace(published, '^(\\d+)/(\\d+)/(\\d+)\\s*.*$', '$3-$1-$2') end as released, max(cast(cvss as FLOAT)) as cvss, max(cast(cvss3 as FLOAT)) as cvss3, report_date FROM raw_patch as a0 WHERE report_date = ${report_date} and datediff(${report_date}, cast(case when published rlike '^(\\d+)-(\\d+)-(\\d+)\\s*(\\d+)\\s*.*$' then regexp_replace(published, '^(\\d+)-(\\d+)-(\\d+)\\s*.*$', '$1-$2-$3') else regexp_replace(published, '^(\\d+)/(\\d+)/(\\d+)\\s*.*$', '$3-$1-$2') end as DATE) ) >= 0 -- -- -- GROUP BY translate(concat(report_date,qid),'-',''), cast(qid as INT), -- vendorid, -- severity, title, -- cast(hostcount as INT), case when published rlike '^(\\d+)-(\\d+)-(\\d+)\\s*(\\d+)\\s*.*$' then regexp_replace(published, '^(\\d+)-(\\d+)-(\\d+)\\s*.*$', '$1-$2-$3') else regexp_replace(published, '^(\\d+)/(\\d+)/(\\d+)\\s*.*$', '$3-$1-$2') end, -- cast(cvss as FLOAT), -- cast(cvss3 as FLOAT), report_date ;
------------------------------
INSERT OVERWRITE TABLE ref_patchhosts PARTITION (report_date) SELECT translate(concat(a0.report_date,id),'-','') as hostkey, translate(concat(a0.report_date,qid),'-','') as qidkey, cast(qid as INT), a0.ip, a0.dns, a0.netbios, a0.os, a0.oscpe, max(a0.vulncnt), a0.report_date FROM raw_patchhosts as a0 join final_hosts as h0 on (a0.ip = h0.ip and a0.report_date=h0.report_date) WHERE a0.report_date = ${report_date} -- -- -- GROUP BY translate(concat(a0.report_date,id),'-',''), translate(concat(a0.report_date,qid),'-',''), cast(qid as INT), a0.ip, a0.dns, a0.netbios, a0.os, a0.oscpe, -- a0.vulncnt, a0.report_date ;
-------------------------------
INSERT OVERWRITE TABLE kri_servers PARTITION (report_date) select translate(concat(v0.report_date,h0.parentid),'-','') as parentkey, translate(concat(v0.report_date,h0.id),'-','') as hostkey, translate(concat(v0.report_date,v0.qid),'-','') as qidkey, s0.segment, s0.bu, h0.parentid, h0.id, h0.ip, h0.dns, h0.netbios, os, v0.qid, status, v0.type, v0.severity, lastfound, lastscan, released, datediff(v0.report_date, cast(released as date)) as days, v0.report_date
from final_vm as v0 -- on (p0.qidkey = v0.qidkey and v0.report_date = v0.report_date) left join ref_patch as p0 on (p0.qidkey = v0.qidkey and p0.report_date = v0.report_date) join final_kb as k0 on (k0.qidkey = v0.qidkey and k0.report_date = v0.report_date) join final_hosts as h0 on (h0.hostkey = v0.hostkey and h0.report_date = v0.report_date) join final_hosttags as h1 on (h1.hostkey = h0.hostkey and h1.report_date = v0.report_date) join ref_servertags s0 on (s0.tagkey = h1.tagkey and s0.report_date = v0.report_date)
where v0.report_date = ${report_date}
-- denominator -- and lower(s0.segment) like '%asia division%' -- and lastscan is not null -- not sure why I see these, but it's FROM qualys -- and lower(s0.title) not like '%external%'
-- numerator -- and datediff(p0.report_date, cast(released as date)) > 60 -- and v0.type = 'Confirmed' -- and status <> 'Fixed' -- and k0.severity in (4,5) -- and patchable = '1'
group by translate(concat(v0.report_date,h0.parentid),'-',''), translate(concat(v0.report_date,h0.id),'-',''), translate(concat(v0.report_date,v0.qid),'-',''), s0.segment, s0.bu, h0.parentid, h0.id, h0.ip, h0.dns, h0.netbios, os, v0.qid, status, v0.type, v0.severity, lastfound, lastscan, released, datediff(v0.report_date, cast(released as date)), v0.report_date
;
