INSERT OVERWRITE TABLE kri_desktops PARTITION (report_date)
select translate(concat(v0.report_date,h0.parentid),'-','') as parentkey, translate(concat(v0.report_date,h0.id),'-','') as hostkey, translate(concat(v0.report_date,v0.qid),'-','') as qidkey, segment, h0.parentid, h0.id, h0.ip, h0.dns, h0.netbios, os, v0.qid, status, v0.type, v0.severity, lastfound, lastscan, published, datediff(v0.report_date, cast(published as date)) as days, cast(v0.report_date as date) as report_date
from final_vm as v0 join final_kb as k0 on (k0.qidkey = v0.qidkey and k0.report_date = v0.report_date) -- join final_sl as sl0 on (sl0.qidkey = k0.qidkey and sl0.report_date = v0.report_date) join final_hosts as h0 on (h0.hostkey = v0.hostkey and h0.report_date = v0.report_date) join final_hosttags as h1 on (h1.hostkey = h0.hostkey and h1.report_date = v0.report_date) join ref_desktopchild s1 on (s1.tagkey = h1.tagkey and s1.report_date = v0.report_date) join ref_desktoptags s0 on (s0.parentkey = s1.parentkey and s0.report_date = v0.report_date)
where v0.report_date = ${report_date}
-- denominator and h1.name not like 'Type: Servers' and (lower(h0.os) like 'windows%' or lower(h0.os) like 'microsoft%') and lower(h0.os) not like 'windows server%' and lower(h0.os) not like 'windows 2008%' and netbios <> '' and netbios is not null and datediff(v0.report_date, cast(h0.lastscan as date)) <= 7 and h1.name not like 'Bell Desktops'
-- numerator -- and v0.type like 'Confirmed' and status not like 'Fixed' and v0.severity in (4,5) and patchable = '1' -- and datediff(v0.report_date, cast(published as date)) > 60 -- -- and k0.qidkey not in (select qidkey from final_sl where report_date = '2019-09-16' and title like 'VMAR - Desktop Exclusions - Static') -- and (k0.title not like '%Symantec%' and k0.title not like '%AnyConnect%')
group by segment, h0.parentid, h0.id, h0.ip, h0.dns, h0.netbios, os, v0.qid, status, v0.type, v0.severity, lastfound, lastscan, published, datediff(v0.report_date, cast(published as date)), v0.report_date ;
INSERT OVERWRITE TABLE ref_scanreport PARTITION (report_date) SELECT translate(concat(report_date,qid),'-','') as qidkey, translate(concat(report_date,''),'-','') as hostkey, file, ip, network, tracking, dns, netbios, qghostid, os, cpe, qid, type, ssl, results, firstdetected, lastdetected, timesdetected, status, cvssfinal, cvss3final, tags, groups, report_date FROM raw_scanreport where report_date = ${report_date} ;
---------------------------------
INSERT OVERWRITE TABLE ref_oscpes PARTITION (report_date) SELECT concat(report_date,md5(taxonomy)) as taxonomykey, -- translate(concat(report_date, regexp_replace(taxonomy, '["]*', '')),'-','') as taxonomykey, regexp_replace(os, '["]*', '') as os, regexp_replace(cpe, '["]*', '') as cpe, regexp_replace(taxonomy, '["]*', '') as taxonomy, report_date FROM raw_oscpes where report_date = ${report_date} ;
INSERT OVERWRITE TABLE ref_inventory PARTITION (report_date) SELECT concat(report_date,md5(taxonomy)) as taxonomykey, -- translate(concat(report_date, regexp_replace(taxonomy, '["]*', '')),'-','') as taxonomykey, regexp_replace(ip, '["]*', '') as ip, regexp_replace(taxonomy, '["]*', '') as taxonomy, regexp_replace(inv, '["]*', '') as inv, report_date FROM raw_inventory where report_date = ${report_date} ;
------------------------------------
INSERT OVERWRITE TABLE final_taxonomy PARTITION (report_date) SELECT concat(h.report_date,md5( case when i.ip=h.ip then i.taxonomy when o.os=h.os and o.cpe=h.cpe then o.taxonomy else '' end )) as taxonomykey, -- translate(concat(h.report_date,case -- when i.ip=h.ip then i.taxonomy -- when o.os=h.os and o.cpe=h.cpe then o.taxonomy -- else '' -- end),'-','') as taxonomykey, parentkey, hostkey, h.ip, case when i.ip=h.ip then i.taxonomy when o.os=h.os and o.cpe=h.cpe then o.taxonomy else '' end as taxonomy, h.report_date FROM final_hosts as h left join ref_oscpes as o on (o.os=h.os and o.cpe=h.cpe and o.report_date=h.report_date) left join ref_inventory as i on (i.ip=h.ip and i.report_date=h.report_date) where h.report_date = ${report_date} ;
-----------------
INSERT OVERWRITE TABLE final_hostnets PARTITION (report_date) select hostkey, networkkey, hosts.id, dip, hosts.ip, ipam.network, max(mask) as mask, count(hosts.hostkey) as cnt, hosts.report_date from final_hosts as hosts left join ref_subnets as ipam on (ipam.report_date=hosts.report_date and obj='IP4Network') where hosts.report_date=${report_date} and dip between bdip and edip -- and obj='IP4Network' group by hosts.report_date, hostkey, hosts.id, dip, hosts.ip, networkkey, ipam.network order by hosts.report_date, dip, hosts.ip, mask DESC ;
-----------------
INSERT OVERWRITE TABLE ref_reportables PARTITION (report_date) SELECT translate(concat(report_date, reportable),'-','') as reportablekey, concat(report_date,md5(taxonomy)) as taxonomykey, -- translate(concat(report_date, taxonomy),'-','') as taxonomykey, reportable, taxonomy, report_date FROM raw_reportables where report_date = ${report_date} ;
-----------------
INSERT OVERWRITE TABLE final_ports PARTITION (report_date) select * from ( select hostkey, qidkey, -- qid, -- a1 as raw, -- results, cast(case when qid = 82023 then regexp_extract(a1, '\\s*(\\d+)\\s*', 1) when qid = 78005 then regexp_extract(a1, '\\d+\\.\\d+\\.\\d+\\.\\d+[\\t\\s](\\d+)[\\t\\s|]*', 1) when qid = 78006 then regexp_extract(a1, '\\s*(\\d+)$', 1) end as INT) as port, case when qid = 78006 then 'udp' else 'tcp' end as protocol, report_date from final_vm lateral view explode(split(results,'[|]')) a as a1 where report_date = ${report_date} and qid in (82023,78005,78006) and a1 not like '%Address%Port%' and a1 not like 'Port%' and a1 not like '%State%Source%' ) as q where port is not null ;
-----------------
drop table if exists tmp_authexplode; create temporary table if not exists tmp_authexplode as select hosts.parentkey, -- hosts.hostkey, case when sum(case when op.port in (22,135,445,161) or (op.port=443 and qid=216008) or qid in (70053,38307,78049,216008) then 1 else 0 end) > 0 then 1 else 0 end as hasPort, case when sum(case when op.port in (22,135,445,161) then 0 else 1 end) > 0 then 1 else 0 end as noPort, case when sum(case when qid in (70053,38307,78049,216008) then 1 else 0 end) > 0 then 1 else 0 end as hasAuth, case when sum(case when qid in ( 105015,70053,105296, 105053,38307,105297, 105192,78049,105298, 105441,105443,216008 ) then 0 else 1 end) > 0 then 0 else 1 end as noAuthQID, case when sum(case when tracking like 'AGENT' then 1 else 0 end) > 0 then 1 else 0 end as hasAgent, case when sum(case when qid = 70053 then 1 else 0 end) > 0 then 1 else 0 end as Win, case when sum(case when qid = 38307 then 1 else 0 end) > 0 then 1 else 0 end as Unix, case when sum(case when qid = 78049 then 1 else 0 end) > 0 then 1 else 0 end as SNMP, case when sum(case when qid = 216008 then 1 else 0 end) > 0 then 1 else 0 end as VMware, hosts.report_date from final_hosts as hosts left join final_vm as vm on (vm.hostkey=hosts.hostkey) left join final_ports as op on (op.hostkey=hosts.hostkey) where hosts.report_date=${report_date} and vm.type like 'Info%' and qid in ( 105015,70053,105296, 105053,38307,105297, 105192,78049,105298, 105441,105443,216008, 45038 ) group by hosts.parentkey, -- hosts.hostkey, hosts.report_date -- limit 32 ;
INSERT OVERWRITE TABLE final_authhosts PARTITION (report_date) select concat(hn.report_date,md5(taxonomy)) as taxonomykey, -- translate(concat(report_date, taxonomy),'-','') as taxonomykey, ipam.networkkey, hn.network, fh.parentkey, -- collect_set(fh.hostkey) as hostkeys, fh.id, fh.ip, fh.dns, fh.netbios, fh.qghostid, fh.os, fh.cpe, reporting, country, zone, location, owner, ipam.type as category, taxonomy, count(distinct auth.parentkey) as Hosts, sum(hasPort) as HostsWithAuthPorts, sum(hasAuth) as Auth, sum(hasAgent) as Agents, sum(noPort) as NoPorts, sum(noAuthQID) as NoAuthQID, sum(Win) as Win, sum(Unix) as Unix, sum(SNMP) as SNMP, sum(VMware) as VMware, report_date from tmp_authexplode as auth join final_hosts as fh on (auth.parentkey=fh.hostkey) join final_hostnets as hn on (auth.parentkey=hn.hostkey) join ref_subnets as ipam on (hn.networkkey=ipam.networkkey and ipam.obj='IP4Network') left join final_taxonomy as tax on (tax.hostkey=hn.hostkey) where auth.report_date=${report_date} group by hn.report_date, ipam.networkkey, hn.network, fh.parentkey, -- fh.hostkey, fh.id, fh.ip, fh.dns, fh.netbios, fh.qghostid, fh.os, fh.cpe, reporting, country, zone, location, owner, ipam.type, taxonomy order by hn.report_date, ipam.networkkey, hn.network, fh.parentkey, -- fh.hostkey, fh.id, fh.ip, fh.dns, fh.netbios, fh.qghostid, fh.os, fh.cpe, reporting, country, zone, location, owner, ipam.type, taxonomy -- limit 32 ;
INSERT OVERWRITE TABLE final_auth PARTITION (report_date) select taxonomykey, networkkey, network, reporting, country, zone, location, owner, category, taxonomy, count(Hosts) as Hosts, sum(HostsWithAuthPorts) as HostsWithAuthPorts, sum(Auth) as Auth, sum(Agents) as Agents, sum(NoPorts) as NoPorts, sum(NoAuthQID) as NoAuthQID, sum(Win) as Win, sum(Unix) as Unix, sum(SNMP) as SNMP, sum(VMware) as VMware, report_date from final_authhosts as auth where auth.report_date=${report_date} group by report_date, networkkey, network, reporting, country, zone, location, owner, category, taxonomykey, taxonomy order by report_date, networkkey, network, reporting, country, zone, location, owner, category, taxonomy ;

import json
import java.io
import re
from org.apache.commons.io import IOUtils
from java.nio.charset import StandardCharsets
from org.apache.nifi.processor.io import StreamCallback

class PyStreamCallback(StreamCallback):
  def __init__(self):
        pass
  def process(self, inputStream, outputStream):
    text = IOUtils.readLines(inputStream, StandardCharsets.UTF_8)

    reportline = 0;
    z = -1;
    for line in text:
        z = line.find('Patches by Host')
        if z != -1:
          break;
        reportline += 1

    if z != -1:
      for line in text[reportline+2:]:
        if len(line.strip()) == 0:
          break
        outputStream.write(line.strip() + "\n") 

flowFile = session.get()
if (flowFile != None):
  flowFile = session.write(flowFile,PyStreamCallback())
  flowFile = session.putAttribute(flowFile, "filename", flowFile.getAttribute('filename').split('.')[0]+'_translated.json')
  session.transfer(flowFile, REL_SUCCESS)

import json
import java.io
import re
from org.apache.commons.io import IOUtils
from java.nio.charset import StandardCharsets
from org.apache.nifi.processor.io import StreamCallback

class PyStreamCallback(StreamCallback):
        def __init__(self, flowfile):
                self.ff = flowfile
                pass
        def process(self, inputStream, outputStream):
                text = IOUtils.readLines(inputStream, StandardCharsets.UTF_8)
                header = text.pop(0)
                for line in text[1:]:
                        str = line.strip().replace(",,",",\"\",")
                        str = str.strip().replace(",,",",\"\",")
                        outputStream.write(str)
                        if str.endswith('"'):
                                outputStream.write("\n");
                        else:
                                outputStream.write("<BR>")


flowFile = session.get()
if (flowFile != None):
        flowFile = session.write(flowFile,PyStreamCallback(flowFile))
        flowFile = session.putAttribute(flowFile, "filename", flowFile.getAttribute('filename').split('.')[0]+'_translated.json')
        session.transfer(flowFile, REL_SUCCESS)

import json
import java.io
import re
from org.apache.commons.io import IOUtils
from java.nio.charset import StandardCharsets
from org.apache.nifi.processor.io import StreamCallback

class PyStreamCallback(StreamCallback):
  def __init__(self):
        pass
  def process(self, inputStream, outputStream):
    text = IOUtils.readLines(inputStream, StandardCharsets.UTF_8)

    reportline = 0;
    z = -1;
    for line in text:
        z = line.find('Patch List')
        if z != -1:
          break;
        reportline += 1

    if z != -1:
      for line in text[reportline+2:]:
        if len(line.strip()) == 0:
          break
        outputStream.write(line.strip() + "\n") 

flowFile = session.get()
if (flowFile != None):
  flowFile = session.write(flowFile,PyStreamCallback())
  flowFile = session.putAttribute(flowFile, "filename", flowFile.getAttribute('filename').split('.')[0]+'_translated.json')
  session.transfer(flowFile, REL_SUCCESS)

-- Create Data Model
-- Hopefully only needed once. -- DROP TABLE IF EXISTS raw_hosts; -- DROP TABLE IF EXISTS raw_hosttags; -- DROP TABLE IF EXISTS raw_vm; -- DROP TABLE IF EXISTS raw_kb; -- DROP TABLE IF EXISTS raw_maptbl; -- DROP TABLE IF EXISTS raw_cvss; -- DROP TABLE IF EXISTS raw_ags; -- DROP TABLE IF EXISTS raw_tags; -- DROP TABLE IF EXISTS typed_hosts; -- DROP TABLE IF EXISTS typed_hosttags; -- DROP TABLE IF EXISTS typed_vm; -- DROP TABLE IF EXISTS typed_kb; -- DROP TABLE IF EXISTS typed_maptbl; -- DROP TABLE IF EXISTS typed_cvss; -- DROP TABLE IF EXISTS typed_ags; -- DROP TABLE IF EXISTS typed_tags; -- DROP TABLE IF EXISTS unified;
-- RAW DATA
-- detections
-- DROP TABLE IF EXISTS raw_hosts; CREATE EXTERNAL TABLE IF NOT EXISTS raw_hosts ( id INT, qghostid STRING, ip STRING, dns STRING, netbios STRING, os STRING, cpe STRING, tracking STRING, network INT, lastscan STRING, lastvmscan STRING, lastvmauth STRING, lastpcscan STRING ) PARTITIONED BY (report_date DATE) ROW FORMAT SERDE 'org.apache.hadoop.hive.serde2.OpenCSVSerde' WITH SERDEPROPERTIES ( "separatorChar" = ",", "quoteChar" = "'", "escapeChar" = "\\" ) STORED AS TEXTFILE;
-- DROP TABLE IF EXISTS raw_hosttags; CREATE EXTERNAL TABLE IF NOT EXISTS raw_hosttags ( id INT, tagid INT, name STRING ) PARTITIONED BY (report_date DATE) ROW FORMAT SERDE 'org.apache.hadoop.hive.serde2.OpenCSVSerde' WITH SERDEPROPERTIES ( "separatorChar" = ",", "quoteChar" = "'", "escapeChar" = "\\" ) STORED AS TEXTFILE;
-- DROP TABLE IF EXISTS raw_vm; CREATE EXTERNAL TABLE IF NOT EXISTS raw_vm ( id INT, qid INT, status STRING, type STRING, severity INT, results STRING, port INT, protocol STRING, service STRING, ssl STRING, disabled STRING, ignored STRING, arfkernel STRING, arfservice STRING, arfconfig STRING, timesfound STRING, firstfound STRING, lastfound STRING, lastprocessed STRING ) PARTITIONED BY (report_date DATE) ROW FORMAT SERDE 'org.apache.hadoop.hive.serde2.OpenCSVSerde' WITH SERDEPROPERTIES ( "separatorChar" = ",", "quoteChar" = "'", "escapeChar" = "\\" ) STORED AS TEXTFILE;
-- kb
CREATE EXTERNAL TABLE IF NOT EXISTS raw_kb ( qid INT, title STRING, type STRING, severity INT, category STRING, discovery STRING, patchable STRING, pci STRING, diagnosis STRING, consequence STRING, solution STRING, lastmodified STRING, published STRING ) PARTITIONED BY (report_date DATE) ROW FORMAT SERDE 'org.apache.hadoop.hive.serde2.OpenCSVSerde' WITH SERDEPROPERTIES ( "separatorChar" = ",", "quoteChar" = "'", "escapeChar" = "\\" ) STORED AS TEXTFILE;
CREATE EXTERNAL TABLE IF NOT EXISTS raw_maptbl ( id STRING, type STRING, rid STRING, rurl STRING ) PARTITIONED BY (report_date DATE) ROW FORMAT SERDE 'org.apache.hadoop.hive.serde2.OpenCSVSerde' WITH SERDEPROPERTIES ( "separatorChar" = ",", "quoteChar" = "'", "escapeChar" = "\\" ) STORED AS TEXTFILE;
CREATE EXTERNAL TABLE IF NOT EXISTS raw_cvss ( qid int, base DOUBLE, temporal DOUBLE, vector DOUBLE, accessVector DOUBLE, accessComplexity DOUBLE, impactConfidentiality DOUBLE, impactIntegrity DOUBLE, impactAvailability DOUBLE, authentication DOUBLE, exploitability DOUBLE, remediationLevel DOUBLE, reportConfidence DOUBLE ) PARTITIONED BY (report_date DATE) ROW FORMAT SERDE 'org.apache.hadoop.hive.serde2.OpenCSVSerde' WITH SERDEPROPERTIES ( "separatorChar" = ",", "quoteChar" = "'", "escapeChar" = "\\" ) STORED AS TEXTFILE;
-- ags
CREATE EXTERNAL TABLE IF NOT EXISTS raw_ags ( id INT, title STRING, ownerid STRING, ownername STRING, network INT, lastupdate STRING, businessimpact STRING, cvssCDP STRING, cvssTD STRING, cvssCR STRING, cvssIR STRING, cvssAR STRING, ipset STRING, unitids STRING ) PARTITIONED BY (report_date DATE) ROW FORMAT SERDE 'org.apache.hadoop.hive.serde2.OpenCSVSerde' WITH SERDEPROPERTIES ( "separatorChar" = ",", "quoteChar" = "'", "escapeChar" = "\\" ) STORED AS TEXTFILE;
-- tags
